package javaprgms;
class S
{
	final void display()
	{
		System.out.println("Class S");
	}
}
//display() method is declared final in super class hence it cannot be overrided in subclass.
//If a class is declared as final then that class cannot be inherited.
//All the methods in final class are final methods.
//final variable cannot be changed.


public class Final {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double pi=3.14;
		

	}

}
